    int ranNum;
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        int high = Integer.parseInt(jTextFieldHigh.getText() + "");
        int low = Integer.parseInt(jTextFieldLow.getText() + "");
        int guess = Integer.parseInt(jTextFieldGuess.getText() + "");
        int numOfGuesses = Integer.parseInt(jLabelNumGuess.getText() + "");
        numOfGuesses++;
        jLabelNumGuess.setText(numOfGuesses + "");
        boolean win;
        
        // FIRST GUESS?
        if( jTextFieldHigh.isEnabled() ) {
            jTextFieldHigh.setEnabled( false );
            jTextFieldLow.setEnabled( false );
            ranNum = ran(low, high);
            jLabelRanNum.setText(ranNum + "");
        }
        
        // HIGH? LOW? RIGHT?
        check( guess, low, high );
        
        // WON!
        if (guess == ranNum){
            win(numOfGuesses, low, high);
            jLabelNumGuess.setText(numOfGuesses + "");
        }
  
        
    }//GEN-LAST:event_jButton1ActionPerformed

