    public static int ran(int low, int high){
        int ranNum = (int) (Math.random() * (high - low)) + low;
        return ranNum;
    }
    public void check(int guess, int low, int high ){
        if (guess < low || guess > high){
            jLabelAns.setText("INVALID GUESS, PLEASE TRY AGAIN.");
            
        }
        else if ( guess == ranNum ){
            jLabelAns.setText("Good job you answered correctly! Please play again!");
        }
        else {
            jLabelAns.setText("Sorry not quite. You are too " + wrong(ranNum, guess));
            
        }
    } 
    public static String wrong(int ranNum, int guess){
       int ans = ranNum - guess;
       if (ans > 0){
           return "low.";
       }
       else {
           return "high.";
       }
    }
    public void win(int count, int low, int high){
        jTextFieldHigh.setEnabled( true );
        jTextFieldLow.setEnabled( true );
        count = 0;
        ran(low, high);
        
        
    }

